// src/components/BookAppointment.js
import { useState, useEffect } from "react";
import axios from "axios";

function BookAppointment() {
  const [doctors, setDoctors] = useState([]);
  const [form, setForm] = useState({ doctor: "", date: "", time: "", reason: "" });

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/doctors/");
        setDoctors(res.data);
      } catch (err) {
        alert("Error fetching doctors");
      }
    };
    fetchDoctors();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post("http://localhost:5000/api/appointments/book", form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(res.data.message);
    } catch (err) {
      alert(err.response?.data?.message || "Error booking appointment");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <select name="doctor" onChange={handleChange} required>
        <option value="">Select Doctor</option>
        {doctors.map((doc) => (
          <option key={doc._id} value={doc._id}>
            {doc.name} â€“ {doc.specialization}
          </option>
        ))}
      </select>
      <input name="date" type="date" onChange={handleChange} required />
      <input name="time" placeholder="Time (e.g., 10:00 AM)" onChange={handleChange} required />
      <input name="reason" placeholder="Reason for appointment" onChange={handleChange} required />
      <button type="submit">Book Appointment</button>
    </form>
  );
}

export default BookAppointment;
