// src/components/BookAppointment.js
import { useState, useEffect } from "react";
import axios from "axios";

export default function BookAppointment() {
  const [doctors, setDoctors] = useState([]);
  const [formData, setFormData] = useState({ doctor: "", date: "", time: "", reason: "" });
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/doctors");
        setDoctors(res.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchDoctors();
  }, []);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    try {
      await axios.post("http://localhost:5000/api/appointments", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("Appointment booked successfully!");
      setFormData({ doctor: "", date: "", time: "", reason: "" });
    } catch (error) {
      setMessage(error.response?.data?.message || "Error booking appointment");
    }
  };

  return (
    <div>
      <h2>Book Appointment</h2>
      <form onSubmit={handleSubmit}>
        <select name="doctor" value={formData.doctor} onChange={handleChange}>
          <option value="">Select Doctor</option>
          {doctors.map((doc) => (
            <option key={doc._id} value={doc._id}>{doc.name} - {doc.specialization}</option>
          ))}
        </select>
        <input type="date" name="date" value={formData.date} onChange={handleChange} />
        <input type="time" name="time" value={formData.time} onChange={handleChange} />
        <input name="reason" placeholder="Reason" value={formData.reason} onChange={handleChange} />
        <button type="submit">Book</button>
      </form>
      <p>{message}</p>
    </div>
  );
}
